function initialize(){var a={mapTypeId:google.maps.MapTypeId.ROADMAP};map=new google.maps.Map(document.getElementById("mapCanvas"),a),populateMap()}function populateMap(){var b=gon.addresses,c=b.length;for(a=0;a<c;a++)codeAddress(b[a])}function codeAddress(a){map.setZoom(13);var b=new google.maps.Geocoder;b.geocode({address:a},function(a,b){if(b==google.maps.GeocoderStatus.OK)for(i=0;i<a.length;i++){map.setCenter(a[i].geometry.location);var c=new google.maps.Marker({map:map,position:a[i].geometry.location});markersArray.push(c)}else alert("Geocode fail!  This is why: "+b)})}function clearOverlays(){if(markersArray)for(i=0;i<markersArray.length;i++)markersArray[i].setMap(null)}var map,markersArray=[];